          <tr>
            <td
              style={{
                width: '100%',
                paddingRight: `${direction === 'rtl' ? '0' : '25px'}`,
                paddingLeft: `${direction === 'rtl' ? '25px' : '0'}`,
              }}
            >
              <Input size="large" placeholder="Discount Coupon" />
            </td>
            <td
              style={{
                paddingRight: `${direction === 'rtl' ? '0' : '25px'}`,
                paddingLeft: `${direction === 'rtl' ? '25px' : '0'}`,
              }}
            >
              <Button>Apply</Button>
            </td>
            <td>
              <Button type="primary">
                <Link to={'/dashboard/checkout'}>Checkout</Link>
              </Button>
            </td>
          </tr>
        </tfoot>
      </table>
    </ProductsTable>
  );
}
