import styled from 'styled-components';
import { themeGet } from '@styled-system/theme-get';
import React from 'react';
import PropTypes from 'prop-types';
import singlePostBgImg from '@hotel/assets/images/single-post-bg.jpg';
import ServiceListingWrapper from './Service.style';
import ServiceSectionTitle from './SectionTitle/SectionTitle';
import Container from '@iso/ui/UI/Container/Container';
export default function ServiceListing(){
    return(
        <ServiceListingWrapper>
          <Container fluid="true">
          <ServiceSectionTitle title="Combo hiện có"/>
          <CarouselSection>
          {data.length !== 0 ? (
            <GlideCarousel
              carouselSelector="explore_carousel"
              prevButton={<IoIosArrowBack />}
              nextButton={<IoIosArrowForward />}
              options={carouselOptions}
            >
              <>
                {data.map((post, index) => (
                  <GlideSlide key={index}>
                    <ImageCard
                      link="post-service"
                      imageSrc={post.locationImage.url}
                      title={`Tên dịch vụ`}
                      meta={`100000VND`}
                      sextype={`Dành cho nam`}
                    />
                  </GlideSlide>
                ))}
              </>
            </GlideCarousel>
          ) : (
            <Loader />
          )}
        </CarouselSection>
        </Container>
           
        </ServiceListingWrapper>
        
    );
}
