import React from 'react';
import TextLink from '@iso/ui/TextLink/TextLink';
import Rating from '@iso/ui/Rating/Rating';
import Favourite from '@iso/ui/Favorite/Favorite';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import GridCard from '../GridCard/GridCard';

const responsive = {
  desktop: {
    breakpoint: {
      max: 3000,
      min: 1024,
    },
    items: 1,
    paritialVisibilityGutter: 40,
  },
  mobile: {
    breakpoint: {
      max: 464,
      min: 0,
    },
    items: 1,
    paritialVisibilityGutter: 30,
  },
  tablet: {
    breakpoint: {
      max: 1024,
      min: 464,
    },
    items: 1,
    paritialVisibilityGutter: 30,
  },
};

const PostGrid = (
  props
) => {
  return (
    <GridCard
      isCarousel={true}
      favorite={
        <Favourite
          onClick={event => {
            console.log(event);
          }}
        />
      }
      
      title={<TextLink link={`${proplink}/${props.name}`} content={props.name} />}
      price={`Giá : ${props.price} VNĐ` }
      priceDiscount={`Giá ưu đãi : ${props.price_discount} VNĐ`}
      
      viewDetailsBtn={
        <TextLink link={`${link}/${props.name}`} content="View Details" />
      }
    >
      <Carousel
        additionalTransfrom={0}
        arrows
        autoPlaySpeed={3000}
        containerClass="container"
        dotListClass=""
        draggable
        focusOnSelect={false}
        infinite
        itemClass=""
        renderDotsOutside={false}
        responsive={responsive}
        showDots={true}
        sliderClass=""
        slidesToSlide={1}
      >
        
      </Carousel>
    </GridCard>
  );
};

export default PostGrid;
