import React from 'react';
import Heading from '@iso/ui/Heading/Heading';
import TextLink from '@iso/ui/TextLink/TextLink';
import Container from '@iso/ui/UI/Container/Container';
import SectionGridService from '@hotel/components/SectionGrid/SectionGridService.cra';
import { HotelPostGridLoader } from '@iso/ui/ContentLoader/ContentLoader';
import useWindowSize from '@iso/lib/hooks/useWindowSize';
import useDataApi from '@iso/lib/hooks/useDataApi';
import SectionTitle from '@hotel/components/SectionTitle/SectionTitle';
import {
  LISTING_POSTS_PAGE,
  SINGLE_POST_PAGE,
} from '../../../settings/constant';

const LuxaryHotelsGrid = () => {
  const { data, loading } = useDataApi('/data/hotel.json');
  const { width } = useWindowSize();

  let posts = data;
  let limit;

  if (data && width <= 767) {
    posts = data.slice(0, 4);
    limit = 4;
  }
  if (data && width >= 768) {
    posts = data.slice(0, 6);
    limit = 6;
  }
  if (data && width >= 992) {
    posts = data.slice(0, 8);
    limit = 8;
  }
  if (data && width >= 1200) {
    posts = data.slice(0, 10);
    limit = 10;
  }
  if (data && width >= 1800) {
    posts = data.slice(0, 12);
    limit = 12;
  }

  return (
    <Container fluid={true}>
      <SectionTitle
        title={<Heading content="Top dịch vụ nổi bật" />}
        link={<TextLink link={LISTING_POSTS_PAGE} content="Show all" />}
      />

      <SectionGridService
        link={SINGLE_POST_PAGE}
        columnWidth={[1 / 2, 1 / 2, 1 / 3, 1 / 4, 1 / 5, 1 / 5, 1 / 6]}
        data={posts}
        loading={loading}
        limit={limit}
        placeholder={<HotelPostGridLoader />}
      />
    </Container>
  );
};

export default LuxaryHotelsGrid;
