import React , { useState }from 'react';
import TableWrapper from '../AntTables.styles';
import categories from '../../category';
import FilterDropdown from '@iso/components/Tables/FilterDropdown';
import { Table } from 'antd';
import { TabPane } from '@iso/components/uielements/tabs';
import { Tag } from 'antd';
import { Button } from 'antd';
import { FormWrapper } from '../AntTables.styles';
import { Form ,Input} from 'antd'; 
import { backgroundColor, color, marginRight } from 'styled-system';
const FormTest = () => {
    return(
        <form style={{display:'none',position:'absolute',top:'100px',width:'100px',height:'600px'}}>
    
           <label>Application Name : </label>
           <input type="text"> </input>
  
           <label> id : </label>
           <input type="text" ></input>
  
           <label>Server details : </label>
           <input ></input>
  
           <button>Create</button>
        </form> 
    );
};
export default function() {
  
    const [open, setIsOpen] = React.useState(false);

    const openForm = () => setIsOpen(!open);
  
  const columns = [
    {
      title: 'Tên thể loại',
      dataIndex: 'name',
      key: 'name',
      width: "30%"  ,
      
    },
    {
      title: 'Ghi chú',
      dataIndex: 'note',
      key: 'note',
      width: "30%" ,
      ellipsis:true
    },
    {
        title: '',
        dataIndex: 'action',
        width: '50px',
        render: (_, record) => (
          <>
            {record.name === 'initial' && <Button icon="plus" shape="circle" />}
            {record.name !== 'initial' && (
              <>
              <Button icon="search"  shape="circle" style={{backgroundColor: "#008CBA" ,marginRight:"5px"}} />
              <Button icon="edit" shape="circle" style={{backgroundColor: "#4CAF50" ,marginRight:"5px"}} />
              <Button icon="delete" shape="circle" type="danger" />
              </>
            )}
          </>
        )
      }

  ];
  const myColTitleStyle = {
    textOverflow: 'ellipsis',
    overflow: "hidden",
    whiteSpace: 'nowrap',
    
  };
  return (
    <div >
    <Button type="primary" onClick={openForm} style={{ marginBottom: '3%' }}>
        Thêm thể loại mới +
    </Button>
   
    {open ? (
      <FormWrapper >
        <Form name="basic"
        
        
        style={{display:'',position:'fixed',left:'50%',zIndex:'1',
        top:'50%',width:'75%',height:'600px',boxShadow:'5px 10px 8px #888888',opacity:'1',transform:' translate(-50%, -50%)'}}>
        <Form.Item
        label="Username"
        name="username"
        rules={[{ required: true, message: 'Please input your username!' }]}
      >
        <Input />
      </Form.Item>

      <Form.Item
        label="Password"
        name="password"
        rules={[{ required: true, message: 'Please input your password!' }]}
      >
        <Input.Password />
      </Form.Item>

        
        </Form>
 
      </FormWrapper>
    ) :null
           
}
        
          
    
    <TableWrapper dataSource={categories.data.data} columns={columns}   />
    
    </div>
  );
}
