import React from 'react';
import TableWrapper from '../../AntTables/AntTables.styles'
import services from '../../services';
import { Table } from 'antd';
import { TabPane } from '@iso/components/uielements/tabs';
import { Tag } from 'antd';
import { Button } from 'antd';
import { backgroundColor, color, marginRight } from 'styled-system';
import { FormWrapper,ViewWrapper } from '../../AntTables/AntTables.styles';
import { Form,Input,Checkbox } from 'antd';
import product_employee from '../../product_employee';

export default function() {
    const FormAddService= ()=>{
  
    };
    const blur_style=""
    const [open, setIsOpen] = React.useState(false);
    const openForm = () => {setIsOpen(!open)
    };
    const columns = [
      {
        title: 'Tên nhân viên',
        dataIndex: 'username',
        key: 'name',
        width: "20%"  ,
        
      },
      {
        title: 'Tài khoản nhân viên',
        dataIndex: 'email',
        key: 'price',
        width: "20%" ,
        ellipsis:true
      },
      
      {
        title: 'Vị trí',
        dataIndex: 'role',
        key: 'description',
        width: "20%" ,
        ellipsis:true,
        
       
        
      },
      
      
      {
        title: '',
        dataIndex: 'action',
        width: '10%',
        render: (_, record) => (
          <>
            {record.name === 'initial' && <Button icon="plus" shape="circle" />}
            {record.name !== 'initial' && (
              <>
              <Button icon="search" shape="circle" style={{backgroundColor: "#008CBA" ,marginRight:"5px"}} />
              <Button icon="delete" shape="circle" type="danger" />
              </>
            )}
          </>
        )
      }
  
    ];
    const myColTitleStyle = {
      textOverflow: 'ellipsis',
      overflow: "hidden",
      whiteSpace: 'nowrap',
      
    };
    
    return (
      <>
      <ViewWrapper style={{filter:blur_style}}>
      <Button type="primary"  onClick={openForm} style={{ marginBottom: '3%' }}>
          Thêm nhân viên mới +
      </Button>
      <TableWrapper dataSource={product_employee.data.data} columns={columns}   />
      </ViewWrapper>
      {open ? (
        <FormWrapper style={{backdropFilter:'blur(3px)'}} >
          <Form name="basic">
          <Form.Item  
          label="Tên nhân viên"
          name="name"
          rules={[{ required: true, message: 'Please input your username!' }]}
        >
          <Input />
        </Form.Item>
        <Form.Item
          label="Email nhân viên"
          name="price"
          rules={[{ required: true, message: 'Please input your username!' }]}
        >
          <Input  />
        </Form.Item>
        <Form.Item
          label="Vị trí"
          name="role"
          rules={[{ required: true, message: 'Please input your username!' }]}
        >
          <Checkbox
              
            >
              Nhân viên bán hàng
          </Checkbox>
          <Checkbox
              
            >
              Nhân viên dịch vụ
            </Checkbox>
        </Form.Item>
       
      <Button   style={{ marginBottom: '1%',backgroundColor: "#4CAF50" ,marginRight:"5px",marginTop:"10px"  }}>
          Thêm
      </Button>
      <Button type="primary"  style={{ marginBottom: '1%' ,marginRight:"5px",marginTop:"10px"  }}>
          Hủy
      </Button>
        </Form>
          
        </FormWrapper>
      ) :null
             
  }
      </>
    );
  }
