import React from 'react';
import TableWrapper from '../../AntTables/AntTables.styles'
import { Link } from 'react-router-dom';
import { Button } from 'antd';
import { backgroundColor, color, marginRight } from 'styled-system';
import { FormWrapper,ViewWrapper } from '../../AntTables/AntTables.styles';
import { Drawer,Descriptions,Badge , Modal,Tag} from 'antd';
import { useSelector } from 'react-redux';

import AddEmployeeView from './ModalView/AddEmployeeView';
import service_employee from'../../service_employee';
export default function() {
  const [state, setState] = React.useState({
    dataList: service_employee.data.data,
    filterDropdownVisible: false,
    searchText: '',
    filtered: false,
    product:{},
  });
  const { initialInvoices, invoices } = useSelector(state => state.Invoices);
  const [visibleInfo, setVisibleInfo] = React.useState(false);
  const showDrawerInfo =()=>{
    setVisibleInfo(true);
  };
  const handleCancelDrwerInfo = () => {
    setVisibleInfo(false);
  };
  const [visibleDeleteModal, setVisibleDeleteModal] = React.useState(false);
  const showModalDelete = () => {
    setVisibleDeleteModal(true);
  };

  const handleOkDeleteModal = () => {
    setModalText('The modal will be closed after two seconds');
    setConfirmLoading(true);
    setTimeout(() => {
      setVisibleDeleteModal(false);
      setConfirmLoading(false);
    }, 2000);
  };

  const handleCancelDeleteModal = () => {
    console.log('Clicked cancel button');
    setVisibleDeleteModal(false);
  };
  //
  const [visible, setVisible] = React.useState(false);
  const [confirmLoading, setConfirmLoading] = React.useState(false);
  const [modalText, setModalText] = React.useState('Content of the modal');

  const showModal = () => {
    setVisible(true);
  };

  const handleOk = () => {
    setModalText('The modal will be closed after two seconds');
    setConfirmLoading(true)
    setTimeout(() => {
      setVisible(false);
      setConfirmLoading(false);
    }, 2000);
  };

  const handleCancel = () => {
    console.log('Clicked cancel button');
    setVisible(false);
  };
  const columns = [
    {
      title: 'Tên nhân viên',
      dataIndex: 'username',
      key: 'name',
      width: "20%"  ,
      
    },
    {
      title: 'Tài khoản nhân viên',
      dataIndex: 'email',
      key: 'price',
      width: "20%" ,
      ellipsis:true
    },
    
    {
      title: 'Vị trí',
      dataIndex: 'role',
      key: 'description',
      width: "20%" ,
      ellipsis:true,
      
     
      
    },
    {
      title: 'Tình trạng',
      
      key: 'status',
      width: "20%" ,
      render:() =>{
        return(
          <Tag color="green" key="Đang làm">
            Đang làm 
            </Tag>
        )

      }
    },
    
    {
      title: '',
      dataIndex: 'action',
      width: '10%',
      render: (_, record) => (
        <>
          {record.name === 'initial' && <Button icon="plus" shape="circle" />}
          {record.name !== 'initial' && (
            <>
            <Link to={`/dashboard/employee_management/${record.id}`}>
            <Button icon="search" shape="circle" style={{marginRight:"5px"}} />
            </Link>
            
              
            <Button icon="delete" onClick={showModalDelete} shape="circle" type="danger" />
            <Modal
                title="Xác nhận"
               
                visible={visibleDeleteModal}
                onOk={handleOkDeleteModal}
                onCancel={handleCancelDeleteModal}
                okText="Xác nhận"
                cancelText="Hủy"
                okType="danger" >
                Xóa nhân viên này?
            </Modal>
            </>
          )}
        </>
      )
    }

  ];
  const myColTitleStyle = {
    textOverflow: 'ellipsis',
    overflow: "hidden",
    whiteSpace: 'nowrap',
    
  };
  
  return (
    <>
    <ViewWrapper>
    <Button type="primary"  onClick={showModal} style={{ marginBottom: '3%' }}>
        Thêm nhân viên mới +
    </Button>
    <TableWrapper dataSource={invoices} columns={columns}   />
    </ViewWrapper>
    
    <Modal
        title="Thêm nhân viên"
        visible={visible}
        onOk={handleOk}
        confirmLoading={confirmLoading}
        onCancel={handleCancel}
        okText="Thêm"
        cancelText="Hủy"
    >
      <AddEmployeeView/>
      </Modal>
    </>
  );
}
