import React, { Fragment, useState } from 'react';
import { useLocation } from '@iso/lib/hooks/useLocation';
import Sticky from 'react-stickynode';
import Row from '@iso/ui/Antd/Grid/Row';
import Col from '@iso/ui/Antd/Grid/Col';
import Modal from '@iso/ui/Antd/Modal/Modal';
import Button from '@iso/ui/Antd/Button/Button';
import Container from '@iso/ui/UI/Container/Container';
import Loader from '@hotel/components/Loader/Loader';
import useWindowSize from '@iso/lib/hooks/useWindowSize';
import Description from '../SinglePage/Description/Description';

import Location from '../SinglePage/Location/Location';
import Review from '../SinglePage/Review/Review';


import TopBar from '../SinglePage/TopBar/TopBar';
import SinglePageWrapper, { PostImage } from './ServiceSinglePage.style';
import PostImageGallery from '../SinglePage/ImageGallery/ImageGallery';
import useDataApi from '@iso/lib/hooks/useDataApi';
import isEmpty from 'lodash/isEmpty';
import SinglePageServiceWrapper from './ServiceSinglePage.style';
const ServiceSinglePage = ({ match }) => {
  const { href } = useLocation();
  const [isModalShowing, setIsModalShowing] = useState(false);
  // useWindowSize hook
  const { width } = useWindowSize();

  let url = '/data/hotel-single.json';
  if (!match.params.slug) {
    url += match.params.slug;
  }
  const { data, loading } = useDataApi(url);
  if (isEmpty(data) || loading) return <Loader />;
  const {
    reviews,
    rating,
    ratingCount,
    price,
    title,
    gallery,
    location,
    content,
   
    author,
  } = data[0];

  return (
    <SinglePageServiceWrapper>
      <PostImage>
        <Button
          type="primary"
          onClick={() => setIsModalShowing(true)}
          className="image_gallery_button"
        >
          View Photos
        </Button>
        <Modal
          visible={isModalShowing}
          onCancel={() => setIsModalShowing(false)}
          footer={null}
          width="100%"
          maskStyle={{
            backgroundColor: 'rgba(255, 255, 255, 0.95)',
          }}
          wrapClassName="image_gallery_modal"
          closable={false}
        >
          <Fragment>
            <PostImageGallery />
            <Button
              onClick={() => setIsModalShowing(false)}
              className="image_gallery_close"
            >
              <svg width="16.004" height="16" viewBox="0 0 16.004 16">
                <path
                  id="_ionicons_svg_ios-close_2_"
                  d="M170.4,168.55l5.716-5.716a1.339,1.339,0,1,0-1.894-1.894l-5.716,5.716-5.716-5.716a1.339,1.339,0,1,0-1.894,1.894l5.716,5.716-5.716,5.716a1.339,1.339,0,0,0,1.894,1.894l5.716-5.716,5.716,5.716a1.339,1.339,0,0,0,1.894-1.894Z"
                  transform="translate(-160.5 -160.55)"
                  fill="#909090"
                />
              </svg>
            </Button>
          </Fragment>
        </Modal>
      </PostImage>

      <TopBar title={title} shareURL={href} author={author} media={gallery} />

      <Container>
        <Row gutter={30} id="reviewSection" style={{ marginTop: 30 }}>
          <Col xl={16}>
            <Description
              content={content}
              title={title}
              location={location}
              rating={rating}
              ratingCount={ratingCount}
            />
            
            <Location location={data[0]} />
          </Col>
          
        </Row>
        <Row gutter={30}>
          <Col xl={16}>
            <Review
              reviews={reviews}
              ratingCount={ratingCount}
              rating={rating}
            />
          </Col>
          <Col xl={8} />
        </Row>
      </Container>
    </SinglePageServiceWrapper>
  );
};

export default ServiceSinglePage;
