import React from 'react';
import TableWrapper from '../BookingManagement.style';


import { ViewWrapper } from '../BookingManagement.style';
import services from '../../Tables/services';
import { Drawer,Descriptions,Badge ,Modal,Col,Row,Form,Input,Checkbox} from 'antd';
import { Button } from 'antd';
import { StatusTag } from '../../Invoice/Invoice.styles';
export default class SimpleView extends React.Component{
  state = { visible: false };
  showDrawer = () => {
    this.setState({
      visible: true,
    });
  };
  onClose = () => {
    this.setState({
      visible: false,
    });
  };
  render(){
    const columns = [
      {
        title: 'Mã dịch vụ',
        dataIndex: 'name',
        key: 'name',
        width: "10%"  ,
        
      },
      {
        title: 'Tên dịch vụ',
        dataIndex: 'price',
        key: 'price',
        width: "10%" ,
        ellipsis:true
      },
      {
        title: 'Ngày làm',
        dataIndex: 'price_discount',
        key: 'price_discount',
        width: "7%" ,
        
      },
      {
        title: 'Tên KH',
        dataIndex: 'price_discount',
        key: 'price_discount',
        width: "10%" ,
        
      },
      {
        title: 'Tình trạng',
        dataIndex: 'bookingStatus',
        rowKey: 'bookingStatus',
        width: '13%',
        render: (text, orderStatus) => {
          let className;
          if (text === 'shipped' || text === 'Shipped' || text === 'SHIPPED') {
            className = 'shipped';
          } else if (
            text === 'delivered' ||
            text === 'Delivered' ||
            text === 'DELIVERED'
          ) {
            className = 'delivered';
          } else if (
            text === 'pending' ||
            text === 'Pending' ||
            text === 'PENDING'
          ) {
            className = 'pending';
          }
          return <StatusTag className="delivered">Hoàn thành</StatusTag>;
        },
      },
      {
        title: '',
        dataIndex: 'action',
        width: '10%',
        render: (_,record) => (
          
            
              <>
              <Button icon="search" onClick={this.showDrawer} shape="circle" style={{marginRight:"5px"}} />{' '}
                <Drawer
                title="Thông tin nhân viên"
                width={720}
                visible={this.state.visible}
                onClose={this.onClose} 
               
                bodyStyle={{ paddingBottom: 80 }}
                >
                  <Descriptions title="" layout="vertical" bordered>
                  <Descriptions.Item label="Tên">{record.name}</Descriptions.Item>
                  <Descriptions.Item label="Tài khoản">{record.email} </Descriptions.Item>
                  <Descriptions.Item label="Vị trí">Nhân viên bán hàng</Descriptions.Item>
                  <Descriptions.Item label="Thời gian làm việc">18:00:00 - 21:00:00</Descriptions.Item>
                  <Descriptions.Item label="Thời gian nghỉ" span={1}>
                    19:00:00-19:15:00
                  </Descriptions.Item>
                  <Descriptions.Item label="Tình trạng" span={3}>
                    <Badge status="processing" text="Đang làm việc" />
                  </Descriptions.Item>
                 
                </Descriptions>
  
                </Drawer>
              <Button icon="delete" shape="circle" type="danger" />
              <Modal
                  title="Xác nhận"
                 

                  okText="Xác nhận"
                  cancelText="Hủy"
                  okType="danger" >
                  Xóa nhân viên này?
              </Modal>
              </>
            
          
        )
      }
  
    ];
    const myColTitleStyle = {
      textOverflow: 'ellipsis',
      overflow: "hidden",
      whiteSpace: 'nowrap',
      
    };
    
    return (
      <>
      <ViewWrapper>
      <TableWrapper dataSource={services.data.data} columns={columns}   />
      </ViewWrapper>
      <Drawer
            title="Create a new account"
            width={720}
            
            bodyStyle={{ paddingBottom: 80 }}
            footer={
              <div
                style={{
                  textAlign: 'right',
                }}
              >
                <Button  style={{ marginRight: 8 }}>
                  Cancel
                </Button>
                <Button  type="primary">
                  Submit
                </Button>
              </div>
            }
          >
            <Form layout="vertical" hideRequiredMark>
              <Row gutter={16}>
                <Col span={16}>
                  <Form.Item
                    name="name"
                    label="Tên"
                    rules={[{ required: true, message: 'Please enter user name' }]}
                  >
                    <Input placeholder="Please enter user name" />
                  </Form.Item>
                </Col>
                
              </Row>
              <Row gutter ={16}>
              <Col span={12}>
                  <Form.Item
                    name="price"
                    label="Giá"
                    rules={[{ required: true, message: 'Nhập giá' }]}
                  >
                    <Input
                      style={{ width: '100%' }}
                      
                      placeholder="Nhập giá"
                    />
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    name="price_discount"
                    label="Giá ưu đãi"
                    rules={[{ required: true, message: 'Nhập giá' }]}
                  >
                    <Input
                      style={{ width: '100%' }}
                      
                      placeholder="Nhập giá"
                    />
                  </Form.Item>
                </Col>
              </Row>
              <Row gutter ={16}>
              <Col span={12}>
                  <Form.Item
                    name="image"
                    label="Hình ảnh (link)"
                    rules={[{ required: true, message: '' }]}
                  >
                    <Input
                      style={{ width: '100%' }}
                      
                      placeholder="Url"
                    />
                  </Form.Item>
                </Col>
                <Col span={12}>
                  <Form.Item
                    name="time_estimate"
                    label="Thời gian thực hiện"
                    rules={[{ required: true, message: 'Nhập thời gian thực hiện' }]}
                  >
                    <Input
                      style={{ width: '100%' }}
                      
                      placeholder="Nhập thời gian"
                    />
