import React from 'react';
import TableWrapper from '../AntTables.styles';
import services from '../../services';
import { Table } from 'antd';
import { TabPane } from '@iso/components/uielements/tabs';
import { Tag } from 'antd';
import { Button } from 'antd';
import { backgroundColor, color, marginRight } from 'styled-system';
import { FormWrapper,ViewWrapper } from '../AntTables.styles';
import { Form,Input,Checkbox } from 'antd';

export default function() {
  const [visibleDeleteModal, setVisibleDeleteModal] = React.useState(false);
  const showModalDelete = () => {
    setVisibleDeleteModal(true);
  };

  const handleOkDeleteModal = () => {
    setModalText('The modal will be closed after two seconds');
    setConfirmLoading(true);
    setTimeout(() => {
      setVisibleDeleteModal(false);
      setConfirmLoading(false);
    }, 2000);
  };

  const handleCancelDeleteModal = () => {
    console.log('Clicked cancel button');
    setVisibleDeleteModal(false);
  };
  //
  const [visible, setVisible] = React.useState(false);
  const [confirmLoading, setConfirmLoading] = React.useState(false);
  const [modalText, setModalText] = React.useState('Content of the modal');

  const showModal = () => {
    setVisible(true);
  };

  const handleOk = () => {
    setModalText('The modal will be closed after two seconds');
    setConfirmLoading(true)
    setTimeout(() => {
      setVisible(false);
      setConfirmLoading(false);
    }, 2000);
  };

  const handleCancel = () => {
    console.log('Clicked cancel button');
    setVisible(false);
  };
  const columns = [
    {
      title: 'Tên dịch vụ',
      dataIndex: 'name',
      key: 'name',
      width: "10%"  ,
      
    },
    {
      title: 'Giá',
      dataIndex: 'price',
      key: 'price',
      width: "10%" ,
      ellipsis:true
    },
    {
      title: 'Giá ưu đãi',
      dataIndex: 'price_discount',
      key: 'price_discount',
      width: "7%" ,
      
    },
    {
      title: 'Mô tả',
      dataIndex: 'description',
      key: 'description',
      width: "20%" ,
      ellipsis:true,
      
     
      
    },
    {
      title: 'Hình ảnh',
      dataIndex: 'picture',
      key: 'picture',
      width: "10%" ,
        
    },
    {
      title: 'Thời gian thực hiện',
      dataIndex: 'time_estimate',
      key: 'time-estimate',
      width: "10%" ,
      
      
    },
    {
      title: 'Book online',
      dataIndex: 'can_book_online',
      key: 'address',
      width: "10%" ,
      ellipsis:true,
      render : bookonline=>{
        if (bookonline == 0){
          return(
          <td key={bookonline}>
                Không
          </td>
          );
        }
        else{
          return(
            <td  key={bookonline}>
                Có
          </td>
          );
        }
      }
    },
    {
       title: 'Dành cho',
       dataIndex: 'sex_type',
       key: 'sex_type',
       width: "10%" ,
       ellipsis:true,
       render : sextype=>{
        if (sextype == 1){
          return(
          <td >
                Nữ
          </td>
          );
        }
        else{
          return(
            <td  >
                Nam
          </td>
          );
        }
      }
    },
    {
      title: '',
      dataIndex: 'action',
      width: '50px',
      render: (_, record) => (
        <>
          {record.name === 'initial' && <Button icon="plus" shape="circle" />}
          {record.name !== 'initial' && (
            <>
            <Button icon="search" shape="circle" style={{backgroundColor: "#008CBA" ,marginRight:"5px"}} />
            <Button icon="delete" onClick={showModalDelete}  shape="circle" type="danger" />
            <Modal
                title="Xác nhận"
               
                visible={visibleDeleteModal}
                onOk={handleOkDeleteModal}
                onCancel={handleCancelDeleteModal}
                okText="Xác nhận"
                cancelText="Hủy"
                okType="danger" >
                Xóa nhân viên này?
            </Modal>
            </>
          )}
        </>
      )
    }

  ];
  const myColTitleStyle = {
    textOverflow: 'ellipsis',
    overflow: "hidden",
    whiteSpace: 'nowrap',
    
  };
  
  return (
    <>
    <ViewWrapper style={{filter:blur_style}}>
    <Button type="primary"  onClick={openForm} style={{ marginBottom: '3%' }}>
        Thêm dịch vụ mới +
    </Button>
    <TableWrapper dataSource={services.data.data} columns={columns}   />
    </ViewWrapper>
    {open ? (
      <FormWrapper >
        <Form name="basic">
        <Form.Item  
        label="Tên dịch vụ"
        name="name"
        rules={[{ required: true, message: 'Please input your username!' }]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        label="Giá"
        name="price"
        rules={[{ required: true, message: 'Please input your username!' }]}
      >
        <Input  />
      </Form.Item>
      <Form.Item
        label="Giá ưu đãi"
        name="price_discount"
        rules={[{ required: true, message: 'Please input your username!' }]}
      >
        <Input  />
      </Form.Item>
      <Form.Item
        label="Mô tả"
        name="description"
        rules={[{ required: true, message: 'Please input your username!' }]}
      >
        <Input style={{height : '200px'}} />
      </Form.Item>
      <Form.Item
        label="Hình ảnh"
        name="picture"
        rules={[{ required: true, message: 'Please input your username!' }]}
      >
        <Input  />
      </Form.Item>
      <Form.Item
        label="Thời gian thực hiện"
        name="time_estimate"
        rules={[{ required: true, message: 'Please input your username!' }]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        label="Booking online"
        name="bookonline"
        rules={[{ required: true, message: 'Please input your username!' }]}
      >
        <Input />
      </Form.Item>
      <Form.Item
        label="Giá ưu đãi"
        name="price_discount"
        rules={[{ required: true, message: 'Please input your username!' }]}
      >
        <Checkbox
            
          >
            Nam
        </Checkbox>
        <Checkbox
            
          >
            Nữ
          </Checkbox>
      </Form.Item>
      <Button   style={{ marginBottom: '1%',backgroundColor: "#4CAF50" ,marginRight:"5px",marginTop:"10px"  }}>
        Thêm
    </Button>
    <Button type="primary"  style={{ marginBottom: '1%' ,marginRight:"5px",marginTop:"10px"  }}>
        Hủy
    </Button>
      </Form>
        
      </FormWrapper>
    ) :null
           
}
    </>
  );
}
