import React from 'react';
import TableWrapper from '../AntTables.styles';
import services from '../../services';
import { Button , Drawer,Col,Row,Select} from 'antd';
import { FormWrapper,ViewWrapper } from '../AntTables.styles';
import { Form,Input,Checkbox } from 'antd';
import AddServiceView from './ModalView/AddServiceView';
import { Modal } from 'antd';
export default function() {
  const [visibleDeleteModal, setVisibleDeleteModal] = React.useState(false);
  const showModalDelete = () => {
    setVisibleDeleteModal(true);
  };

  const handleOkDeleteModal = () => {
    setModalText('The modal will be closed after two seconds');
    setConfirmLoading(true);
    setTimeout(() => {
      setVisibleDeleteModal(false);
      setConfirmLoading(false);
    }, 2000);
  };

  const handleCancelDeleteModal = () => {
    console.log('Clicked cancel button');
    setVisibleDeleteModal(false);
  };
  //
  const [visible, setVisible] = React.useState(false);
  const [confirmLoading, setConfirmLoading] = React.useState(false);
  const [modalText, setModalText] = React.useState('Content of the modal');

  const showModal = () => {
    setVisible(true);
  };

  const handleOk = () => {
    setModalText('The modal will be closed after two seconds');
    setConfirmLoading(true)
    setTimeout(() => {
      setVisible(false);
      setConfirmLoading(false);
    }, 2000);
  };

  const handleCancel = () => {
    console.log('Clicked cancel button');
    setVisible(false);
  };
  const columns = [
    {
      title: 'Tên dịch vụ',
      dataIndex: 'name',
      key: 'name',
      width: "10%"  ,
      
    },
    {
      title: 'Giá',
      dataIndex: 'price',
      key: 'price',
      width: "10%" ,
      ellipsis:true
    },
    {
      title: 'Giá ưu đãi',
      dataIndex: 'price_discount',
      key: 'price_discount',
      width: "7%" ,
      
    },
    {
      title: 'Mô tả',
      dataIndex: 'description',
      key: 'description',
      width: "20%" ,
      ellipsis:true,
      
     
      
    },
    {
      title: 'Hình ảnh',
      dataIndex: 'picture',
      key: 'picture',
      width: "10%" ,
        
    },
    {
      title: 'Thời gian thực hiện',
      dataIndex: 'time_estimate',
      key: 'time-estimate',
      width: "10%" ,
      
      
    },
    {
      title: 'Book online',
      dataIndex: 'can_book_online',
      key: 'address',
      width: "10%" ,
      ellipsis:true,
      render : bookonline=>{
        if (bookonline == 0){
          return(
          <td key={bookonline}>
                Không
          </td>
          );
        }
        else{
          return(
            <td  key={bookonline}>
                Có
          </td>
          );
        }
      }
    },
    {
       title: 'Dành cho',
       dataIndex: 'sex_type',
       key: 'sex_type',
       width: "10%" ,
       ellipsis:true,
       render : sextype=>{
        if (sextype == 1){
          return(
          <td >
                Nữ
          </td>
          );
        }
        else{
          return(
            <td  >
                Nam
          </td>
          );
        }
      }
    },
    {
      title: '',
      dataIndex: 'action',
      width: '10%',
      render: (_, record) => (
        <>
          {record.name === 'initial' && <Button icon="plus" shape="circle" />}
          {record.name !== 'initial' && (
            <>
            <Button icon="search" shape="circle" style={{backgroundColor: "#008CBA" ,marginRight:"5px"}} />
            <Button icon="delete" onClick={showModalDelete}  shape="circle" type="danger" />
            <Modal
                title="Xác nhận"
               
                visible={visibleDeleteModal}
                onOk={handleOkDeleteModal}
                onCancel={handleCancelDeleteModal}
                okText="Xác nhận"
                cancelText="Hủy"
                okType="danger" >
                Xóa dịch vụ này?
            </Modal>
            </>
          )}
        </>
      )
    }

  ];
  const myColTitleStyle = {
    textOverflow: 'ellipsis',
    overflow: "hidden",
    whiteSpace: 'nowrap',
    
  };
  
  return (
    <>
    <ViewWrapper >
    <Button type="primary"  onClick={showModal} style={{ marginBottom: '3%' }}>
        Thêm dịch vụ mới +
    </Button>
    <TableWrapper dataSource={services.data.data} columns={columns}   />
    </ViewWrapper>
    <Drawer
          title="Create a new account"
          width={720}
          onClose={handleCancel}
          visible={visible}
          bodyStyle={{ paddingBottom: 80 }}
          footer={
            <div
              style={{
                textAlign: 'right',
              }}
            >
              <Button onClick={handleCancel} style={{ marginRight: 8 }}>
                Cancel
              </Button>
              <Button onClick={handleCancel} type="primary">
                Submit
              </Button>
            </div>
          }
        >
          <Form layout="vertical" hideRequiredMark>
            <Row gutter={16}>
              <Col span={16}>
                <Form.Item
                  name="name"
                  label="Tên"
                  rules={[{ required: true, message: 'Please enter user name' }]}
                >
                  <Input placeholder="Please enter user name" />
                </Form.Item>
              </Col>
              
            </Row>
            <Row gutter ={16}>
            <Col span={12}>
                <Form.Item
                  name="price"
                  label="Giá"
                  rules={[{ required: true, message: 'Nhập giá' }]}
                >
                  <Input
                    style={{ width: '100%' }}
                    
                    placeholder="Nhập giá"
                  />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="price_discount"
                  label="Giá ưu đãi"
                  rules={[{ required: true, message: 'Nhập giá' }]}
                >
                  <Input
                    style={{ width: '100%' }}
                    
                    placeholder="Nhập giá"
                  />
                </Form.Item>
              </Col>
            </Row>
            <Row gutter ={16}>
            <Col span={12}>
                <Form.Item
                  name="image"
                  label="Hình ảnh (link)"
                  rules={[{ required: true, message: '' }]}
                >
                  <Input
                    style={{ width: '100%' }}
                    
                    placeholder="Url"
                  />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="time_estimate"
                  label="time_estimate"
                  rules={[{ required: true, message: 'Nhập thời gian thực hiện' }]}
                >
                  <Input
                    style={{ width: '100%' }}
                    
                    placeholder="Nhập thời gian"
                  />
                </Form.Item>
              </Col>
            </Row>
            <Row gutter ={16}>
            <Col span={12}>
                <Form.Item
                  name="Đặt online"
                  label="price"
                  rules={[{ required: true, message: 'Nhập giá' }]}
                >
                  <Checkbox>
                    Có
                  </Checkbox>
                  <Checkbox>
                    Không
                  </Checkbox>
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="Dành cho"
                  label="price_discount"
                  rules={[{ required: true, message: 'Nhập giá' }]}
                >
                  <Checkbox>
                    Nam
                  </Checkbox>
                  <Checkbox>
                    Nữ
                  </Checkbox>
                </Form.Item>
              </Col>
            </Row>
            <Row gutter={16}>
              <Col span={12}>
                <Form.Item
                  name="owner"
                  label="Owner"
                  rules={[{ required: true, message: 'Please select an owner' }]}
                >
                  <Select placeholder="Please select an owner">
                    <Option value="xiao">Xiaoxiao Fu</Option>
                    <Option value="mao">Maomao Zhou</Option>
                  </Select>
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item
                  name="type"
                  label="Type"
                  rules={[{ required: true, message: 'Please choose the type' }]}
                >
                  <Select placeholder="Please choose the type">
                    <Option value="private">Private</Option>
                    <Option value="public">Public</Option>
                  </Select>
                </Form.Item>
              </Col>
            </Row>
            <Row gutter={16}>
              <Col span={12}>
                <Form.Item
                  name="approver"
                  label="Approver"
                  rules={[{ required: true, message: 'Please choose the approver' }]}
                >
                  <Select placeholder="Please choose the approver">
                    <Option value="jack">Jack Ma</Option>
                    <Option value="tom">Tom Liu</Option>
                  </Select>
                </Form.Item>
              </Col>
              <Col span={12}>
                
              </Col>
            </Row>
            <Row gutter={16}>
              <Col span={24}>
                <Form.Item
                  name="description"
                  label="Description"
                  rules={[
                    {
                      required: true,
                      message: 'please enter url description',
                    },
                  ]}
                >
                  <Input.TextArea rows={4} placeholder="please enter url description" />
                </Form.Item>
              </Col>
            </Row>
          </Form>
        </Drawer>
    </>
  );
}
