import React from "react";
import TableWrapper from "../../AntTables/AntTables.styles";

import { Button } from "antd";
import DrawerDetailService from "./Drawer";
import { backgroundColor, color, marginRight } from "styled-system";
import { FormWrapper, ViewWrapper } from "../../AntTables/AntTables.styles";
import { Drawer, Descriptions, Badge, Modal } from "antd";
import services_1 from "../../services";
import products from "../../product";
import service_employee from "../../service_employee";

import AddEmployeeView from "../../EmployeeTable/TableView/ModalView/AddEmployeeView";

export default function() {
  const [visibleInfo, setVisibleInfo] = React.useState(false);
  const product={};
  const showDrawerInfo = () => {
    setVisibleInfo(true);
  };
  const handleCancelDrwerInfo = () => {
    setVisibleInfo(false);
  };
  const [visibleDeleteModal, setVisibleDeleteModal] = React.useState(false);
  const showModalDelete = () => {
    setVisibleDeleteModal(true);
  };

  const handleOkDeleteModal = () => {
    setModalText("The modal will be closed after two seconds");
    setConfirmLoading(true);
    setTimeout(() => {
      setVisibleDeleteModal(false);
      setConfirmLoading(false);
    }, 2000);
  };

  const handleCancelDeleteModal = () => {
    console.log("Clicked cancel button");
    setVisibleDeleteModal(false);
  };
  //
  const [visible, setVisible] = React.useState(false);
  const [confirmLoading, setConfirmLoading] = React.useState(false);
  const [modalText, setModalText] = React.useState("Content of the modal");

  const showModal = () => {
    setVisible(true);
  };

  const handleOk = () => {
    setModalText("The modal will be closed after two seconds");
    setConfirmLoading(true);
    setTimeout(() => {
      setVisible(false);
      setConfirmLoading(false);
    }, 2000);
  };

  const handleCancel = () => {
    console.log("Clicked cancel button");
    setVisible(false);
  };
  const columns = [
    {
      title: "ID",
      dataIndex: "id",
      key: "id",
      width: "5%",
    },
    {
      title: "Tên sản phẩm",
      dataIndex: "name",
      key: "name",
      width: "10%",
    },
    {
      title: "Giá",
      dataIndex: "price",
      key: "price",
      width: "10%",
      ellipsis: true,
    },
    {
      title: "Giá ưu đãi",
      dataIndex: "price_discount",
      key: "price_discount",
      width: "7%",
    },
    {
      title: "Mô tả",
      dataIndex: "description",
      key: "description",
      width: "20%",
      ellipsis: true,
    },
    {
      title: "Hình ảnh",
      dataIndex: "picture",
      key: "picture",
      width: "10%",
    },

    {
      title: "Action",
      dataIndex: "action",
      width: "10%",
      render: (text, record ) => (
        <>
          <Button
            key={`${record.id}`}
            icon="search"
            onClick={() => {showDrawerInfo();
            }}
            shape="circle"
            style={{ backgroundColor: "#008CBA", marginRight: "5px" }}
          />
        
          <Button
            icon="delete"
            onClick={showModalDelete}
            shape="circle"
            type="danger"
          />
          <Modal
            title="Xác nhận"
            visible={visibleDeleteModal}
            onOk={handleOkDeleteModal}
            onCancel={handleCancelDeleteModal}
            okText="Xác nhận"
            cancelText="Hủy"
            okType="danger"
          >
            Xóa sản phẩm này?
          </Modal>
        </>
      ),
    },
  ];
  const myColTitleStyle = {
    textOverflow: "ellipsis",
    overflow: "hidden",
    whiteSpace: "nowrap",
  };

  return (
    <>
      <ViewWrapper>
        <Button
          type="primary"
          onClick={showModal}
          style={{ marginBottom: "3%" }}
        >
          Thêm sản phẩm mới +
        </Button>
        <TableWrapper  rowKey={record => record.id} dataSource={products.data.data} columns={columns} />
      </ViewWrapper>
      
      <Modal
        title="Thêm nhân viên"
        visible={visible}
        onOk={handleOk}
        confirmLoading={confirmLoading}
        onCancel={handleCancel}
        okText="Thêm"
        cancelText="Hủy"
      >
        <AddEmployeeView />
      </Modal>
      <Drawer  
        closable={false}
        title="Thông tin  sản phẩm"
        width={720}
        visible={visibleInfo}
        onClose={handleCancelDrwerInfo}
        bodyStyle={{ paddingBottom: 80 }}
      >
        <Descriptions title="" layout="vertical" bordered>
          <Descriptions.Item label="Tên dịch vụ" span={12}>{record.name}</Descriptions.Item>
          <Descriptions.Item label="Giá" span={1}></Descriptions.Item>
          <Descriptions.Item label="Giá ưu đãi" span={1}></Descriptions.Item>
          <Descriptions.Item label="Dành cho" span={1}></Descriptions.Item>
          <Descriptions.Item label="Đặt online" span={1}></Descriptions.Item>
          <Descriptions.Item label="Tình trạng" span={3}>
            <Badge status="processing" text="Còn trống" />
          </Descriptions.Item>
          <Descriptions.Item label="Mô tả"></Descriptions.Item>
        </Descriptions>
      </Drawer>
    </>
  );
}
