import React from 'react';
import TableWrapper from '../../AntTables/AntTables.styles'
import services from '../../services';
import { Table } from 'antd';
import { TabPane } from '@iso/components/uielements/tabs';
import { Tag } from 'antd';
import { Button } from 'antd';
import { backgroundColor, color, marginRight } from 'styled-system';
import { FormWrapper,ViewWrapper } from '../../AntTables/AntTables.styles';
import { Form,Input,Checkbox,Modal,Select } from 'antd';
import product_employee from '../../product_employee';

export default function() {
  
  const [visibleDeleteModal, setVisibleDeleteModal] = React.useState(false);
  const showModalDelete = () => {
    setVisibleDeleteModal(true);
  };

  const handleOkDeleteModal = () => {
    setModalText('The modal will be closed after two seconds');
    setConfirmLoading(true);
    setTimeout(() => {
      setVisibleDeleteModal(false);
      setConfirmLoading(false);
    }, 2000);
  };

  const handleCancelDeleteModal = () => {
    console.log('Clicked cancel button');
    setVisibleDeleteModal(false);
  };
  //
  const [visible, setVisible] = React.useState(false);
  const [confirmLoading, setConfirmLoading] = React.useState(false);
  const [modalText, setModalText] = React.useState('Content of the modal');

  const showModal = () => {
    setVisible(true);
  };

  const handleOk = () => {
    setModalText('The modal will be closed after two seconds');
    setConfirmLoading(true)
    setTimeout(() => {
      setVisible(false);
      setConfirmLoading(false);
    }, 2000);
  };

  const handleCancel = () => {
    console.log('Clicked cancel button');
    setVisible(false);
  };
    const columns = [
      {
        title: 'Tên nhân viên',
        dataIndex: 'username',
        key: 'name',
        width: "20%"  ,
        
      },
      {
        title: 'Tài khoản nhân viên',
        dataIndex: 'email',
        key: 'price',
        width: "20%" ,
        ellipsis:true
      },
      
      {
        title: 'Vị trí',
        dataIndex: 'role',
        key: 'description',
        width: "20%" ,
        ellipsis:true,
        
       
        
      },
      
      
      {
        title: '',
        dataIndex: 'action',
        width: '10%',
        render: (_, record) => (
          <>
            {record.name === 'initial' && <Button icon="plus" shape="circle" />}
            {record.name !== 'initial' && (
              <>
              <Button icon="search" shape="circle" style={{backgroundColor: "#008CBA" ,marginRight:"5px"}} />
              <Button icon="delete" onClick={showModalDelete} shape="circle" type="danger" />
              <Modal
               title="Xác nhận"
               visible={visibleDeleteModal}
               onOk={handleOkDeleteModal}
               onCancel={handleCancelDeleteModal}
               okText="Xác nhận"
               cancelText="Hủy" >
                <p>Xác nhận xóa nhân viên này?</p>
            </Modal>
              </>
            )}
          </>
        )
      }
  
    ];
    const myColTitleStyle = {
      textOverflow: 'ellipsis',
      overflow: "hidden",
      whiteSpace: 'nowrap',
      
    };
    
    return (
      <>
      <ViewWrapper >
      <Button type="primary"  onClick={showModal} style={{ marginBottom: '3%' }}>
          Thêm nhân viên mới +
      </Button>
      <TableWrapper dataSource={product_employee.data.data} columns={columns}   />
      </ViewWrapper>
      <Modal
        title="Thêm nhân viên"
        visible={visible}
        onOk={handleOk}
        confirmLoading={confirmLoading}
        onCancel={handleCancel}
        okText="Thêm"
        cancelText="Hủy"
    >
      <FormWrapper >
    <Form name="basic">
    <Form.Item  
    label="Tên nhân viên"
    name="name"
    rules={[{ required: true, message: 'Please input your username!' }]}
    >
    <Input />
  </Form.Item>
  <Form.Item
    label="Email nhân viên"
    name="price"
    rules={[{ required: true, message: 'Please input your username!' }]}
  >
    <Input  />
  </Form.Item>
  <Form.Item
    label="Vị trí"
    name="role"
    rules={[{ required: true, message: 'Please input your username!' }]}
  >
    <Select
          placeholder="Select a option and change input text above"
         
          allowClear
    >
          <Option value="male">Nhân viên bán hàng</Option>
          <Option value="female">Nhân viên bán hàng</Option>
          
    </Select>
  </Form.Item>
 

  </Form>
    
  </FormWrapper>
      </Modal>
      </>
    );
  }
