import React , { useState }from 'react';
import TableWrapper from '../AntTables.styles';
import categories from '../../category';
import { Table } from 'antd';
import { TabPane } from '@iso/components/uielements/tabs';
import { Tag } from 'antd';
import { Button } from 'antd';
import FormItem from 'antd/lib/form/FormItem';
import { backgroundColor, color, marginRight } from 'styled-system';
const ProfessionImage = ({ src }) => {
    const props = useSpring({ opacity: 1, from: { opacity: 0 } });
    return (
        <animated.img
            src={src}
            alt=""
            style={{ width: "250px", height: "250px", ...props }}
        />
    );
};
export default function() {
  
    const [open, setIsOpen] = React.useState(false);

    const openForm = () => setIsOpen(!open);
  
  const columns = [
    {
      title: 'Tên thể loại',
      dataIndex: 'name',
      key: 'name',
      width: "30%"  ,
      
    },
    {
      title: 'Ghi chú',
      dataIndex: 'note',
      key: 'note',
      width: "30%" ,
      ellipsis:true
    },
    {
        title: '',
        dataIndex: 'action',
        width: '50px',
        render: (_, record) => (
          <>
            {record.name === 'initial' && <Button icon="plus" shape="circle" />}
            {record.name !== 'initial' && (
              <>
              <Button icon="search"  shape="circle" style={{backgroundColor: "#008CBA" ,marginRight:"5px"}} />
              <Button icon="edit" shape="circle" style={{backgroundColor: "#4CAF50" ,marginRight:"5px"}} />
              <Button icon="delete" shape="circle" type="danger" />
              </>
            )}
          </>
        )
      }

  ];
  const myColTitleStyle = {
    textOverflow: 'ellipsis',
    overflow: "hidden",
    whiteSpace: 'nowrap',
    
  };
  return (
    <div style={{position:'relative'}}>
    <Button type="primary" onClick={openForm} style={{ marginBottom: '3%' }}>
        Thêm thể loại mới +
    </Button>
   
    {open===true && (
           <form style={{display:'none',position:'absolute',top:'100px',width:'100px',height:'600px'}}>
    
           <label>Application Name : </label>
           <input type="text"> </input>
  
           <label> id : </label>
           <input type="text" ></input>
  
           <label>Server details : </label>
           <input ></input>
  
           <button>Create</button>
        </form> )
           
}
        
          
    
    <TableWrapper dataSource={categories.data.data} columns={columns}   />
    
    </div>
  );
}
